public class ArithmeticExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numberOne=8;
		int theBestNumber=100;
		numberOne+=10;
		numberOne--;
		theBestNumber*=numberOne;
		theBestNumber++;
		System.out.println("NumberOne:"+numberOne);
		System.out.println("the best number:"+theBestNumber);
	}

}
